const runtimeGame = runtimeScene.getGame();
runtimeGame.scenes = runtimeGame.scenes || {};
const name = eventsFunctionContext.getArgument("sceneName");

for(let layout of runtimeGame.getGameData().layouts) {
    if(layout.name === name) {
        const newScene = new gdjs.RuntimeScene(runtimeGame);
        newScene.loadFromScene(layout);
        runtimeGame.scenes[name] = newScene;
        newScene.onPause(); // Scene is paused for now
        break;
    }
}

const runtimeGame = runtimeScene.getGame();
runtimeGame.scenes = runtimeGame.scenes || {};
const name = eventsFunctionContext.getArgument("sceneName");
const newScene = runtimeGame.scenes[name];

if(newScene) {
    runtimeScene.onPause();
    newScene.onResume();
    runtimeGame._sceneStack._stack.push(newScene);
}

const runtimeGame = runtimeScene.getGame();
runtimeGame.scenes = runtimeGame.scenes || {};

if (runtimeGame._sceneStack._stack.length <= 1) return null;

runtimeGame._sceneStack._stack.pop();

// Tell the new current scene it's being resumed
const currentScene = runtimeGame._sceneStack._stack[runtimeGame._sceneStack._stack.length - 1];
if (currentScene) {
    currentScene.onResume();
}
